import React, { useState, useEffect } from "react";
import EditOrCreateOpinion from "./EditOrCreateOpinion";

export default function UpdateOpinions(props) {
  const [successMessage, setSuccessMessage] = useState(null);

  const [ready, setReady] = useState(false);
  const [res, setRes] = useState();

  useEffect(() => {
    const getAllOpinions = async () => {
      const allOpinionsRes = await Axios.get(
        `${domain}/opinion/getallmyn/${props.shel.MA}`
      );
      setRes(allOpinionsRes.data);
      setReady(true);
    };
    getAllOpinions();
  }, []);

  return ready ? (
    <div className="col">
      <h2>רשימת כל החוודים ע"פ תקופות:</h2>
      {res.map((opinion) => (
        <>
          <div>
            {
              <EditOrCreateOpinion
                suc={props.setSuccessMessage}
                oid={opinionid}
              />
            }
          </div>
          <br />
        </>
      ))}
    </div>
  ) : (
    <div>
      טוען את כל החוו"דים שלו/ה מהשרת... (רוב הסיכויים שאם אתה מספיק לקרוא את
      ההודעה הזאת אז יש תקלה בשרת)
    </div>
  );
}
