import React, { useState } from "react";
import ShowOpinion from "./ShowOpinion";
import Modal from "react-modal";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    overflow: "auto",
    maxHeight: "100vh",
  },
};

export default function OpinionSumu(props) {
  let tkufaNum = props.opinion.Tkufa;
  let TkufaYear = tkufaNum % 2 === 0 ? tkufaNum / 2 : tkufaNum / 2 + 0.5;
  let tkufainYear = tkufaNum % 2 === 0 ? "2" : "1";
  let yearString = TkufaYear.toString();
  let countD = 0;
  for (let i = 0; i < 4 - yearString.length; i++) countD++;
  let addex = "";
  for (let i = 0; i < countD; i++) addex = addex + "0";
  let finilTkuda = tkufainYear + "." + addex + yearString;

  const [modalIsOpen, setIsOpen] = useState(false);

  function openModal() {
    setIsOpen(true);
  }

  function afterOpenModal() {}

  function closeModal() {
    setIsOpen(false);
  }

  return (
    <>
      <div>
        <button className="OpinionOpen" onClick={openModal}>
          {finilTkuda}
        </button>
      </div>

      <div>
        <Modal
          isOpen={modalIsOpen}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <ShowOpinion id={props.opinion._id}></ShowOpinion>
        </Modal>
      </div>

      <div>
        <button className="OpinionClose" onClick={closeModal}>
          סגור את חוו"ד {finilTkuda}
        </button>
      </div>
    </>
  );
}
