  function nshow11() {
    setShown11(false);
  }
  function nshow12() {
    setShown12(false);
  }

  return (
    <>
      <table className="otable">
        <tbody>
          <tr className="tr">
            <th className="oth" colSpan="2">
              משוב אישי (כהערכה כוללת)
            </th>
            <th className="oth" colSpan="7">
              {"נכשל < - -  - > ממוצע < - -  - > מצוין"}
            </th>
          </tr>
          <tr className="rowWithHistory">
            <th className="oth" rowSpan="6">
              למידה וקוגניציה
            </th>
            <th className="oth" onMouseOver={show4} onMouseLeave={nshow4}>
              למידה
            </th>
            <GradeRow
              isGray={props.isGray}
              allDATA={props.allDATA}
              avgs={props.avgs}
              grade={props.c1}
              criteria="C1"
            />
          </tr>
          <tr className="rowWithHistory">
            <th className="oth" onMouseOver={show5} onMouseLeave={nshow5}>
              תכנון
            </th>
            <GradeRow
              isGray={props.isGray}
              allDATA={props.allDATA}
              avgs={props.avgs}
              grade={props.c2}
              criteria="C2"
            />
          </tr>
          <tr className="rowWithHistory">
            <th className="oth" onMouseOver={show6} onMouseLeave={nshow6}>
              תפיסה מרחבית
            </th>
            <GradeRow
              isGray={props.isGray}
              allDATA={props.allDATA}
              avgs={props.avgs}
              grade={props.c3}
              criteria="C3"
            />
          </tr>
          <tr className="rowWithHistory">
            <th className="oth" onMouseOver={show7} onMouseLeave={nshow10}>
              חלק"ש
