              isGray={props.isGray}
              allDATA={props.allDATA}
              avgs={props.avgs}
              grade={props.c4}
              criteria="C4"
            />
          </tr>
          <tr className="rowWithHistory">
            <th className="oth" onMouseOver={show8} onMouseLeave={nshow11}>
              תקשורת
            </th>
            <GradeRow
              isGray={props.isGray}
              allDATA={props.allDATA}
              avgs={props.avgs}
              grade={props.c5}
              criteria="C5"
            />
          </tr>
          <tr className="rowWithHistory">
            <th className="oth" onMouseOver={show9} onMouseLeave={nshow12}>
              עומס
            </th>
            <GradeRow
              isGray={props.isGray}
              allDATA={props.allDATA}
              avgs={props.avgs}
              grade={props.c6}
              criteria="C6"
            />
          </tr>
          <tr className="rowWithHistory">
            <th className="oth" rowSpan="3">
              יכולות פיקוד וניהול
            </th>
            <th className="oth" onMouseOver={show11} onMouseLeave={nshow11}>
              קבלת החלטות
            </th>
            <GradeRow
              isGray={props.isGray}
              allDATA={props.allDATA}
              avgs={props.avgs}
              grade={props.c7}
              criteria="C7"
            />
          </tr>
          <tr className="rowWithHistory">
            <th className="oth" onMouseOver={show12} onMouseLeave={nshow12}>
              הפעלה
            </th>
