              isGray={props.isGray}
              allDATA={props.allDATA}
              avgs={props.avgs}
              grade={props.c8}
              criteria="C8"
            />
          </tr>
          <tr className="rowWithHistory">
            <th className="oth" onMouseOver={show4} onMouseLeave={nshow4}>
              תחקור
            </th>
            <GradeRow
              isGray={props.isGray}
              allDATA={props.allDATA}
              avgs={props.avgs}
              grade={props.c9}
              criteria="C9"
            />
          </tr>
        </tbody>
      </table>
    </>
  );
}
