import EditOrCreateOpinion from "./EditOrCreateOpinion";
import Modal from "react-modal";
import React, { useState, useEffect } from "react";
import Axios from "axios";
import domain from "../../../../util/domain";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "25%",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    overflow: "auto",
    maxHeight: "100vh",
  },
};

export default function OpinionSumu(props) {
  let tkufaNum = res.Tkufa;
  let TkufaYear = tkufaNum % 2 === 0 ? tkufaNum / 2 : tkufaNum / 2 + 0.5;
  let tkufainYear = tkufaNum % 2 === 0 ? "2" : "1";
  let yearString = TkufaYear.toString();
  let countD = 0;
  for (let i = 0; i < 4 - yearString.length; i++) countD++;
  let addex = "";
  for (let i = 0; i < countD; i++) addex = addex + "0";
  let finilTkuda = tkufainYear + "." + addex + yearString;

  const [modalIsOpen, setIsOpen] = useState(false);

  const [successMessage, setSuccessMessage] = useState(null);

  const [ready, setReady] = useState(false);
  const [res, setRes] = useState();

  useEffect(() => {
    const getAllOpinions = async () => {
      const allOpinionsRes = await Axios.get(
        `${domain}/opinion/getallhis/${props.oid}`
      );
      setRes(allOpinionsRes.data);
      setReady(true);
    };
    getAllOpinions();
  }, []);

  function openModal() {
    setIsOpen(true);
  }

  function afterOpenModal() {}

  function closeModal() {
    setIsOpen(false);
  }

  return (
    <>
      <div>
        <button className="OpinionOpen" onClick={openModal}>
          {finilTkuda}
        </button>
      </div>

      <div>
        <Modal
          isOpen={modalIsOpen}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <EditOrCreateOpinion id={props.oid} forClosing={closeModal} />
        </Modal>
      </div>
    </>
  );
}
