import Axios from "axios";
import React, { useEffect, useState} from "react";
import domain from "../util/domain";
import ErrorMessage from "../components/messages/ErrorMessage";

function Report(props) {
    const [nickname,setNickname] = useState("התחבר לאיתור שם אוטומטי");
    const [ready,setReady] = useState("התחבר לאיתור שם אוטומטי");
    
    const [fname, setfName] = useState();
    const [fcontent, setfContent] = useState();
    const [content, setContent] = useState();

    const [ errorMessage, setErrorMessage ] = useState(null);

    useEffect( () => {
        const getFUD = async () => {
            const FUDRes = await Axios.get(`${domain}/user/getFullDetails`);
            try {setNickname(FUDRes.data.NickName);} catch (err){console.log(err);}
            setReady(true);
        }
        getFUD();
    }, []);

    async function updatefud(e) {
        e.preventDefault();
        const updateFUDData = {
        name: fname,
        content: fcontent,
        };

        try {
        await Axios.put(`${domain}/footer/report`, updateFUDData);
        props.suc("הבעיה דווחה בהצלחה!");
        const done=props.whendone;
        done(false);
        } catch (err) {
        if (err.response) {
            if (err.response.data.errorMessage) {
            setErrorMessage(err.response.data.errorMessage);
            }
        }
        else
            console.log(err);
        }
        return;
    }

  

    return ready ? ( <>
        <div>
        <div className="fudheaderdiv"><h3 className="fudheader">דיווח על בעיה:</h3></div><br />
        {errorMessage && (
            <ErrorMessage
            message={errorMessage}
            clear={() => setErrorMessage(null)}
            />
        )}
        <form>
            <input className="fudinput2"
                id="form-name"
                type="text"
                placeholder="שם"
                defaultValue={nickname}
                value={fname}
                onChange={(e) => setfName(e.target.value)}
            />
            <br/>
            <input className="fudinput2"
                id="form-content"
                type="text"
                placeholder="תיאור הבעיה"
                defaultValue={content}
                value={fcontent}
                onChange={(e) => setfContent(e.target.value)}
            />
        </form>
        <br/>
        <br/>
        <div ><button onClick={updatefud}>
            שלח
        </button></div>
        </div><br/><br/><br/></>):(<h2>טוען את הפרטים האישיים הקיימים שלך מהשרת...</h2>
        
    );
}

export default Report;
