import Axios from "axios";
import React, { useEffect, useState} from "react";
import domain from "../util/domain";
import ErrorMessage from "../components/messages/ErrorMessage";

function Report(props) {
    const [ready,setReady] = useState(false);
    
    const [fname, setfName] = useState();
    const [fcontent, setfContent] = useState();
    const [content, setContent] = useState("התחבר למילוי שם אוטומטי");

    const [ errorMessage, setErrorMessage ] = useState(null);

    useEffect( () => {
        const getFUD = async () => {
            const FUDRes = await Axios.get(`${domain}/user/getFullDetails`);
            try {setfName(FUDRes.data.NickName);} catch (err){console.log(err);}

            setReady(true);
        }
        getFUD();
    }, []);

    async function sendReport(e) {
        e.preventDefault();
        
        if((!fname || fname==="התחבר לאיתור שם אוטומטי")|| !fcontent)
            setErrorMessage("חסר שם או פירוט");

        const reportDetails = {
        name: fname,
        content: fcontent,
        };

        try {
        await Axios.put(`${domain}/footer/report`, reportDetails);
        props.suc("הבעיה דווחה בהצלחה!");
        const done=props.whendone;
        done(false);
        } catch (err) {
        if (err.response) {
            if (err.response.data.errorMessage) {
            setErrorMessage(err.response.data.errorMessage);
            }
        }
        else
            console.log(err);
        }
        return;
    }

  

    return  (<>
        <div>
        <div className="fudheaderdiv"><h3 className="fudheader">דיווח על בעיה:</h3></div><br />
        {errorMessage && (
            <ErrorMessage
            message={errorMessage}
            clear={() => setErrorMessage(null)}
            />
        )}
        <form>
            <div className="footerlabel">שם המדווח: </div>
            <div className="footerinput"><input className="footerinputshort"
                id="form-name"
                type="text"
                placeholder="שם המדווח"
                defaultValue={fname}
                value={fname}
                onChange={(e) => setfName(e.target.value)}
            /> </div>
            <br/>
            <br/>
            <div className="footerlabel">פירוט הבעיה: </div>
            <div className="footerinput"><textarea className="footerinputlong"
                id="form-content"
                type="text"
                placeholder="פירוט הבעיה"
                defaultValue={content}
                value={fcontent}
                onChange={(e) => setfContent(e.target.value)}
            /> </div>
        </form>
        <br/>
        <br/>
        <div className="reportsend"><button className="reportsendbutton" onClick={sendReport}>
            שלח
        </button></div>
        </div><br/><br/><br/>{!ready && <h2>טוען את השם שלך מהשרת...</h2>}
    </>)
    ;
}

export default Report;
